import React, { Component, Fragment } from 'react';
import {
  ToggleBar,
  ToggleItem,
  SectionWrapper,
  FilterWrapper,
  FilterButton,
} from './BodySection.styled.js';

class BodySection extends Component {
  constructor(props){
    super(props);
    this.state={
      currentView: 'snapshot',
      selectedSections: ['premiumRange', 'markets', 'industries', 'products'],
    };

    this.setCurrentView = this.setCurrentView.bind(this);
  }

  setCurrentView(view) {
    this.setState({currentView: view})
  }

  render() {
    return (
      <div>
        <ToggleBar>
          <ToggleItem
            onClick={() => this.setCurrentView('snapshot')}
            selected={this.state.currentView === 'snapshot'}
          >SNAPSHOT</ToggleItem>
          <ToggleItem
            onClick={() => this.setCurrentView('trends')}
            selected={this.state.currentView === 'trends'}
          >TRENDS</ToggleItem>
        </ToggleBar>
        {this.state.currentView === 'trends' ? <div>Sorry Come Back Soon!</div> :
          <SectionWrapper>
            <FilterWrapper>
              <FilterButton>Button One</FilterButton>
              <FilterButton>Button Two</FilterButton>
              <FilterButton>Button Three</FilterButton>
              <FilterButton>Button Four</FilterButton>
            </FilterWrapper>
          </SectionWrapper>
        }
      </div>
    );
  }
}

export default BodySection;
