import React, { Component, Fragment } from 'react';
import { ToggleBar, ToggleItem } from './BodySection.styled.js';

class BodySection extends Component {
  constructor(props){
    super(props);
    this.state={
      currentView: 'snapshot'
    };

    this.setCurrentView = this.setCurrentView.bind(this);
  }

  setCurrentView(view) {
    this.setState({currentView: view})
  }

  render() {
    return (
      <div>
        <ToggleBar>
          <ToggleItem
            onClick={() => this.setCurrentView('snapshot')}
            selected={this.state.currentView === 'snapshot'}
          >SNAP SHOT</ToggleItem>
          <ToggleItem
            onClick={() => this.setCurrentView('trends')}
            selected={this.state.currentView === 'snapshot'}
          >TRENDS</ToggleItem>
        </ToggleBar>
        {this.state.currentView === 'trends' ? <div>Sorry Come Back Soon!</div> :
          <div>Nice! You're looking at snapshots.</div>
        }
      </div>
    );
  }
}

export default BodySection;
