import React, { Component, Fragment } from 'react';
import {
  ToggleBar,
  ToggleItem,
  SectionWrapper,
  FilterWrapper,
  FilterButton,
} from './BodySection.styled.js';

class BodySection extends Component {
  constructor(props){
    super(props);
    this.state={
      currentView: 'snapshot',
      selectedSections: ['premiumRange', 'markets', 'industries', 'products'],
    };

    this.setCurrentView = this.setCurrentView.bind(this);
    this.filterButtonOnClick = this.filterButtonOnClick.bind(this);
  }

  setCurrentView(view) {
    this.setState({currentView: view})
  }

  filterButtonOnClick(section) {
    const currentList = this.state.selectedSections;
    let newList;
    if(currentList.includes(section)) {
      newList = currentList.filter(j => j !== section);
    } else {
      newList = currentList.push(section);
    }
    this.setState({selectedSections: newList});
  }

  render() {
    return (
      <Fragment>
        <ToggleBar>
          <ToggleItem
            onClick={() => this.setCurrentView('snapshot')}
            selected={this.state.currentView === 'snapshot'}
          >SNAPSHOT</ToggleItem>
          <ToggleItem
            onClick={() => this.setCurrentView('trends')}
            selected={this.state.currentView === 'trends'}
          >TRENDS</ToggleItem>
        </ToggleBar>
        {this.state.currentView === 'trends' ? <div>Sorry Come Back Soon!</div> :
          <SectionWrapper>
            <FilterWrapper>
              <FilterButton
                selected={this.state.selectedSections && this.state.selectedSections.includes('premiumRange')}
                onClick={() => this.filterButtonOnClick('premiumRange')}
              >Premium Range</FilterButton>
              <FilterButton
                selected={this.state.selectedSections.includes('markets')}
                onClick={() => this.filterButtonOnClick('markets')}
              >Markets</FilterButton>
              <FilterButton
                selected={this.state.selectedSections.includes('industries')}
                onClick={() => this.filterButtonOnClick('industries')}
              >Induestries</FilterButton>
              <FilterButton
                selected={this.state.selectedSections.includes('products')}
                onClick={() => this.filterButtonOnClick('products')}
              >Products</FilterButton>
            </FilterWrapper>
          </SectionWrapper>
        }
      </Fragment>
    );
  }
}

export default BodySection;
