import React, { Component, Fragment } from 'react';
import { Doughnut } from 'react-chartjs-2';
import {
  SectionWrapper,
  GroupWrapper,
  CategoryTitle,
  ChartWrapper,
  VisualWrapper,
  LedgendWrapper,
  SelectedData,
  LegendColorTextWrapper,
  LedgendItem,
  LegendColor,
  StyledDoughnut,
  SelectedPercent
} from './SectionData.styled.js';

class SectionData extends Component {
  constructor(props){
    super(props);
    this.state={
      selectedBrokerPremium: this.props.brokerData[0].premium,
      selectedBrokerTitle: this.props.brokerData[0].title,
      selectedBrokerPercent: this.props.carrierData[0].proportion,
      selectedCarrierPremium: this.props.carrierData[0].premium,
      selectedCarrierTitle: this.props.carrierData[0].title,
      selectedCarrierPercent: this.props.carrierData[0].proportion,
    };
  }

  renderDonutChart(dataSet, group) {
    const values = [];
    dataSet.forEach(value => {
      values.push(value.proportion);
    });

    const data = {
      datasets:
        [{
          data: values,
          backgroundColor: ['#02c7b0', '#607d8b', '#30a200', '#013d40', '#006567', '#008f91', '#00afb1', '#00ce5f', '#00e833', '#01ff39#' ],
          borderWidth: 0,
        }],
    }
    const options = {
      maintainAspectRatio: false,
      cutoutPercentage: 80,
      events: [],
      elements: {
        center: {
          text: 'Red',
          color: '#FF6384', // Default is #000000
          fontStyle: 'Arial', // Default is Arial
          sidePadding: 20, // Default is 20 (as a percentage)
          minFontSize: 20, // Default is 20 (in px), set to false and text will not wrap.
          lineHeight: 25 // Default is 25 (in px), used for when text wraps
        }
      }
    }

    return (
      <Fragment>
        <SelectedPercent>
          {group === 'broker' ?
            `${Math.floor(this.state.selectedBrokerPercent)}%`
            : `${Math.floor(this.state.selectedCarrierPercent)}%`
          }
        </SelectedPercent>
        <Doughnut
          data={data}
          options={options}
          height={200}
          width={200}
        />
        <SelectedData>
          {group === 'broker' ? `$${this.state.selectedBrokerPremium}` : `$${this.state.selectedCarrierPremium}`}
        </SelectedData>
        <SelectedData>
          {group === 'broker' ? `${this.state.selectedBrokerTitle}` : `${this.state.selectedCarrierTitle}`}
        </SelectedData>
      </Fragment>
    )
  }

  renderLegend(dataSet) {
    const values = [];
    dataSet.forEach(value => {
      values.push(value);
    });

    const colors = ['#02c7b0', '#607d8b', '#30a200', '#013d40', '#006567', '#008f91', '#00afb1', '#00ce5f', '#00e833', '#01ff39#' ]

    return (
      <Fragment>
        {values.map((value, i) => (
          <LegendColorTextWrapper>
            <LegendColor color={colors[i]}/>
            <LedgendItem>{value.title}</LedgendItem>
          </LegendColorTextWrapper>
        ))}
      </Fragment>
    );
  }

  render() {
    return (
      <SectionWrapper>
        <GroupWrapper>
          <CategoryTitle>{this.props.category}</CategoryTitle>
          <ChartWrapper>
            <LedgendWrapper>
              {this.renderLegend(this.props.brokerData)}
            </LedgendWrapper>
            <VisualWrapper>
              {this.renderDonutChart(this.props.brokerData, "broker")}
            </VisualWrapper>
          </ChartWrapper>
        </GroupWrapper>
        <GroupWrapper>
          <CategoryTitle>{this.props.category}</CategoryTitle>
          <ChartWrapper>
            <LedgendWrapper>
              {this.renderLegend(this.props.carrierData)}
            </LedgendWrapper>
            <VisualWrapper>
              {this.renderDonutChart(this.props.carrierData, "carrier")}
            </VisualWrapper>
          </ChartWrapper>
        </GroupWrapper>
      </SectionWrapper>
    );
  }
}

export default SectionData;
