import styled from 'styled-components'

const ToggleBar = styled.div`
  display: flex;
  flex-direction: row;
`;

const ToggleItem = styled.h1`
  color: ${props => props.selected ? '#00e0c2' : '#013d40'};
  font-size: 24px;
`;

export{
  ToggleBar,
  ToggleItem
};
