import styled from 'styled-components'

const ToggleBar = styled.div`
  display: flex;
  flex-direction: row;
`;

const ToggleItem = styled.p`
  border-bottom: ${props => props.selected ? '2px solid #00e0c2' : 'unset'};
  color: ${props => props.selected ? '#00e0c2' : '#013d40'};
  cursor: pointer;
  font-size: 16px;
  margin: 24px 12px;

  padding-bottom: 24px;
`;

export{
  ToggleBar,
  ToggleItem
};
